mapper.select:
    SELECT pt.PM_TYPE_TYPE pmTypeType , COUNT(*) AS memberNo,
   (SELECT DISTINCT COUNT(*) FROM person_member_base pmb LEFT JOIN person_member pm on pm.MEMBER_NO= pmb.MEMBER_NO WHERE pm.MERCHANT_NO=#{merchantNo})AS memberNoNum
   FROM  person_member_base pmb 
   LEFT JOIN person_member pm ON pmb.MEMBER_NO=pm.MEMBER_NO
   LEFT JOIN pm_type pt ON pm.PM_TYPE_CODE=pt.`CODE`
   WHERE pm.MERCHANT_NO=#{merchantNo}

			and pm.CREATE_DATE  >= #{startTime}


			and pm.CREATE_DATE <= #{endTime}

   GROUP BY pt.PM_TYPE_TYPE

