mapper.select:
  	SELECT t1.MEMBER_NAME memberName,t1.MOBILE mobile, t1.WX_OPENID wxOpenId, t3.NO_WX merchantWxNo 
	FROM person_member_base t1
	INNER JOIN person_member t2 ON t1.MEMBER_NO = t2.MEMBER_NO
	INNER JOIN guid_member t3 ON t3.MEMBER_NO = t2.MEMBER_NO_GM
	WHERE t1.MOBILE = #{mobile,jdbcType=VARCHAR}
	AND t2.MERCHANT_NO = #{merchentNo,jdbcType=VARCHAR}

		AND t3.NO_WX = #{merchantWxNo,jdbcType=VARCHAR}

	limit 1

